import logo from './logo.svg';
import './App.css';

const App = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    // Fetch movies data from your API or a sample API
    // For example, you can use the Open Movie Database (OMDb) API
    const fetchMovies = async () => {
      try {
        const response = await fetch('https://www.omdbapi.com/?s=star wars&apikey=your_api_key');
        const data = await response.json();

        if (data.Search) {
          setMovies(data.Search);
        }
      } catch (error) {
        console.error('Error fetching movies:', error);
      }
    };

    fetchMovies();
  }, []);

  return (
    <div className="app">
      <h1>Movie Database</h1>
      <div className="movies-container">
        {movies.map((movie) => (
          <div key={movie.imdbID} className="movie-card">
            <img src={movie.Poster} alt={movie.Title} />
            <h2>{movie.Title}</h2>
            <p>{movie.Year}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
