import React from 'react';
import './App.css';
import movie1Poster from './tehtavat/Periodi2/projektikuva.png';

const App = () => {
  const movies = [
    {
      title: 'Movie 1',
      poster: movie1Poster,
      year: '2022',
    },
    {
      title: 'Movie 2',
      poster: 'https://via.placeholder.com/150',
      year: '2021',
    },
    {
      title: 'Movie 3',
      poster: 'https://via.placeholder.com/150',
      year: '2020',
    },
  ];

  return (
    <div className="app">
      <h1>Movie Database</h1>
      <div className="movies-container">
        {movies.map((movie, index) => (
          <div key={index} className="movie-card">
            <img src={movie.poster} alt={movie.title} />
            <h2>{movie.title}</h2>
            <p>{movie.year}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
